import React, { useState, useEffect } from "react";
import queryString from "query-string";

const JsonTable1 = () => {
  const [urlData, setUrlData] = useState("");
  const [tableData, setTableData] = useState([]);

  useEffect(() => {
    parseUrl();
  }, [urlData]);

  const parseUrl = () => {
    try {
      const url = new URL(urlData);
      const queryParams = queryString.parse(url.search);

      const tableRows = Object.keys(queryParams).map((key, index) => ({
        stt: index + 1,
        attribute: key,
        dataType: typeof queryParams[key],
        description: "",
        required: "N",
        value: queryParams[key],
      }));

      setTableData(tableRows);
    } catch (error) {
      console.error("Invalid URL format", error);
      setTableData([]);
    }
  };

  const copyToClipboard = () => {
    const tableAsText = tableData.map((row) => {
      const { stt, ...rowDataWithoutStt } = row;
      return rowDataWithoutStt;
    });

    navigator.clipboard.writeText(JSON.stringify(tableAsText, null, 2)).then(() => {
      console.log('Table data copied to clipboard');
    }).catch((error) => {
      console.error('Error copying to clipboard', error);
    });
  };

  return (
    <div>
      <textarea
        rows={10}
        cols={40}
        value={urlData}
        onChange={(e) => setUrlData(e.target.value)}
      />
      <br />
      <button onClick={parseUrl}>Generate Table</button>
      <button onClick={copyToClipboard}>Copy Data</button>

      {/* Display your table or other components based on tableData */}
      <table>
        <thead>
          <tr>
            <th>STT</th>
            <th>Attribute</th>
            <th>DataType</th>
            <th>Description</th>
            <th>Required</th>
            <th>Value</th>
          </tr>
        </thead>
        <tbody>
          {tableData.map((row) => (
            <tr key={row.stt}>
              <td>{row.stt}</td>
              <td>{row.attribute}</td>
              <td>{row.dataType}</td>
              <td>{row.description}</td>
              <td>{row.required}</td>
              <td>{row.value}</td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default JsonTable1;
